function Checker() {
  const [firstShow, setFirstShow] = useState(true);

  const handleInitialFormSubmit = () => {
    setFirstShow(false);
  };

  return (
    <ChakraProvider theme={theme} resetCSS={false}>
      <motion.div
        initial={{ opacity: 0, scale: 0.5 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{
          duration: 0.8,
          delay: 0.5,
          ease: [0, 0.71, 0.2, 1.01],
        }}
      >
        <Box
          display="flex"
          justifyContent="center"
          mt="50px"
          className="checker--box"
        >
          <div className="checker--container">
            <Box
              shadow="2xl"
              height="80vh"
              width="800px"
              borderRadius="10px"
              border="1px solid black"
            >
              <Box>
                <Center>
                  <Text
                    className="top-text"
                    fontWeight="semibold"
                    fontSize="30px"
                  >
                    Symptom Checker
                  </Text>
                </Center>
                <Divider />
                {firstShow ? (
                  <InitialForm onSubmit={handleInitialFormSubmit} />
                ) : (
                  <AfterSubmissionContent />
                )}
              </Box>
            </Box>
          </div>
        </Box>
      </motion.div>
    </ChakraProvider>
  );
}

export default Checker;
